import PizzaService from "../services/pizzasService";

const Pizzas = (router) => {
    const mainUrl = 'pizzas';

    // METODO POST, INSERE PRODUTOS
    router.post(`/${mainUrl}/`, async (req,res) => {
        const {body} = req;     
        const {resp, status} = await PizzaService.createPizzas(body.name, body.value);
